insert into subjects (subject_code, name, credit_points, requisities, 
    anti_requisities, description, content_topics, num_group_assessments, 
    assessment_types) values ("70517", "Equity and Trusts", 
    8, 
    '%5B%2270317c%20%20Real%20Property%22%2C%22%3Ca%20href%3D%5C%22https%3A//handbook.uts.edu.au/subjects/70327.html%5C%22%3E70327%3C/a%3E%20Introduction%20to%20Property%20and%20Commercial%20Law%22%5D', '%5B%2270516%20Equity%20and%20Trusts%22%5D', 
    '%3Cp%3EThis%20subject%20explores%20the%20range%20of%20doctrines%20and%20remedies%20that%20were%20originally%20developed%20in%20the%20Courts%20of%20Chancery%20in%20England%20to%20ameliorate%20the%20harshness%20of%20the%20common%20law%20and%20provide%20remedies%20where%20none%20were%20available%20at%20law.%20Equitable%20principles%20are%20all%20fundamentally%20grounded%20in%20the%20concept%20of%20%27conscience%27.%20Despite%20the%20antiquity%20of%20these%20doctrines%2C%20equitable%20principles%20continue%20to%20have%20enormous%20practical%20relevance%2C%20particularly%20in%20modern%20commercial%20litigation.%20Equitable%20doctrines%20and%20principles%20reach%20into%20many%20areas%20of%20legal%20practice%3A%20family%20law%2C%20superannuation%2C%20wills%20and%20probate%2C%20property%20law%20and%20intellectual%20property%2C%20to%20name%20a%20few.%20But%20it%20is%20in%20civil%20litigation%20that%20a%20solid%20understanding%20of%20equitable%20principles%20and%20remedies%20is%20essential%3A%20no%20litigation%20lawyer%20can%20afford%20not%20to%20be%20aware%20of%20how%20to%20apply%20equitable%20principles%20to%20seek%20the%20valuable%20remedies%20available%20in%20equity.%20The%20subject%20also%20examines%20the%20equitable%20concept%20of%20the%20%27trust%27%20whereby%20an%20interest%20in%20property%20is%20legally%20owned%20by%20one%20party%2C%20but%20held%20for%20the%20benefit%20of%20another%20person%20or%20purpose%20permitted%20by%20law.%20Trusts%20are%20commonly%20used%20in%20both%20private%20arrangements%20and%20increasingly%20for%20commercial%20purposes.%20The%20popularity%20of%20unit%20trusts%20and%20trading%20trusts%20as%20versatile%20commercial%20vehicles%20is%20on%20the%20rise%2C%20as%20there%20are%20particularly%20advantageous%20consequences%20for%20taxation%20and%20insolvency%20through%20the%20use%20of%20a%20trust.%20Significant%20developments%20to%20equitable%20principles%20have%20occurred%20through%20a%20number%20of%20Australian%20High%20Court%20decisions%2C%20particularly%20since%20the%201980s.%20The%20subject%20compares%20Australian%20equity%20with%20other%20common%20law%20jurisdictions%20to%20understand%20international%20divergence%20in%20the%20scope%20of%20certain%20principles.', '%3Cp%3E%3Cstrong%3EPart%201%3A%20HISTORY%20AND%20NATURE%20OF%20EQUITY%3C/strong%3E',
